var util        = require("util");
var Success     = require("functional-validation/success");
var Failure     = require("functional-validation/failure");
var Validator   = require("../validator");

function MinLengthValidator(min, message) {
    this._min = min;
    Validator.call(this, message);
}

util.inherits(MinLengthValidator, Validator);

MinLengthValidator.prototype._min = undefined;

MinLengthValidator.prototype.validate = function(value) {
    if (value.length >= this._min) {
        return new Success(value);
    } else {
        return new Failure(this._message);
    }
};

module.exports = MinLengthValidator;
