var util        = require("util");
var Success     = require("functional-validation/success");
var Failure     = require("functional-validation/failure");
var Validator   = require("../validator");

function NonEmptyValidator(message) {
    Validator.call(this, message);
}

util.inherits(NonEmptyValidator, Validator);

NonEmptyValidator.prototype.validate = function(value) {
    if (value.length) {
        return new Success(value);
    } else {
        return new Failure(this._message);
    }
};

module.exports = NonEmptyValidator;
