var util     = require("util");
var Response = require("../response");

function BadeGatewayResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(BadeGatewayResponse, Response);

BadGatewayResponse.prototype.statusCode = function() {
    return 502;
};

module.exports = BadGatewayResponse;
