var util     = require("util");
var Response = require("../response");

function EntityTooLargeResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(EntityTooLargeResponse, Response);

EntityTooLargeResponse.prototype.statusCode = function() {
    return 413;
};

module.exports = EntityTooLargeResponse;
