var util     = require("util");
var Response = require("../response");

function GatewayTimeoutResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(GatewayTimeoutResponse, Response);

GatewayTimeoutResponse.prototype.statusCode = function() {
    return 504;
};

module.exports = GatewayTimeoutResponse;
