var util     = require("util");
var Response = require("../response");

function HttpVersionNotSupportedResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(HttpVersionNotSupportedResponse, Response);

HttpVersionNotSupportedResponse.prototype.statusCode = function() {
    return 505;
};

module.exports = HttpVersionNotSupportedResponse;
