var util     = require("util");
var Response = require("../response");

function MethodNotAllowedResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(MethodNotAllowedResponse, Response);

MethodNotAllowedResponse.prototype.statusCode = function() {
    return 405;
};

module.exports = MethodNotAllowedResponse;
