var util     = require("util");
var Response = require("../response");

function MovedPermanentlyResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(MovedPermanentlyResponse, Response);

MovedPermanentlyResponse.prototype.statusCode = function() {
    return 301;
};

module.exports = MovedPermanentlyResponse;
