var util     = require("util");
var Response = require("../response");

function PreconditionFailedResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(PreconditionFailedResponse, Response);

PreconditionFailedResponse.prototype.statusCode = function() {
    return 412;
};

module.exports = PreconditionFailedResponse;
