var util     = require("util");
var Response = require("../response");

function SeeOtherResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(SeeOtherResponse, Response);

SeeOtherResponse.prototype.statusCode = function() {
    return 303;
};

module.exports = SeeOtherResponse;
