var util     = require("util");
var Response = require("../response");

function ServiceUnavailableResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(ServiceUnavailableResponse, Response);

ServiceUnavailableResponse.prototype.statusCode = function() {
    return 503;
};

module.exports = ServiceUnavailableResponse;
