var util     = require("util");
var Response = require("../response");

function TemporaryRedirectResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(TemporaryRedirectResponse, Response);

TemporaryRedirectResponse.prototype.statusCode = function() {
    return 307;
};

module.exports = TemporaryRedirectResponse;
