var util     = require("util");
var Response = require("../response");

function TooManyRequestResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(TooManyRequestResponse, Response);

TooManyRequestResponse.prototype.statusCode = function() {
    return 429;
};


module.exports = TooManyRequestResponse;
