var util     = require("util");
var Response = require("../response");

function UnsupportedMediaTypeResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(UnsupportedMediaTypeResponse, Response);

UnsupportedMediaTypeResponse.prototype.statusCode = function() {
    return 415;
};

module.exports = UnsupportedMediaTypeResponse;
