var util     = require("util");
var Response = require("../response");

function UriTooLongResponse(request, response, view) {
    Response.call(this, request, response, view);
};

util.inherits(UriTooLongResponse, Response);

UriTooLongResponse.prototype.statusCode = function() {
    return 415;
};

module.exports = UriTooLongResponse;
