var Rx                         = require("rx");
var rxConfig                   = require("../index");
var staticStrategy             = require("../strategies/static");
var fileStrategy               = require("../strategies/file");
var flatDirectoryStrategy      = require("../strategies/flat-directory");
var recursiveDirectoryStrategy = require("../strategies/recursive-directory");

rxConfig(
    fileStrategy("./data/file.json"),
    flatDirectoryStrategy("./data/lol"),
    recursiveDirectoryStrategy("./data/lol/beep"),
    staticStrategy({ "staticness": true })
).subscribe(function(config) {
    config.get("title").subscribe(console.log);
});
